let nav=document.querySelector(".main-nav"),navToggle=document.querySelector(".main-nav__toggle");navToggle.addEventListener("click",(function(){nav.classList.contains("main-nav--close")?(nav.classList.remove("main-nav--close"),nav.classList.add("main-nav--open")):(nav.classList.add("main-nav--close"),nav.classList.remove("main-nav--open"))}));